package com.xgdfin.rpc.annotation;import com.xgdfin.rpc.RpcRequest;import java.io.IOException;import java.io.ObjectInputStream;import java.io.ObjectOutputStream;import java.net.Socket;/** Copyright (C) 2017  ShenZhen xinLianZhengXin Co.,Ltd All Rights Reserved.* 未经本公司正式书面同意，其他任何个人、团体不得使用、复制、修改或发布本软件.* 版权所有深圳市信联征信有限公司 http://www.credlink.com/*/public class RpcNetTransport {    private String host;    private Integer post;    public RpcNetTransport() {}    public RpcNetTransport(String host, Integer post) {        this.host = host;        this.post = post;    }    public Object send(RpcRequest request) {        Socket socket = null;        ObjectOutputStream outputStream = null;        ObjectInputStream inputStream = null;        Object result = null;        try {            socket = new Socket(host, post);            outputStream = new ObjectOutputStream(socket.getOutputStream());            outputStream.writeObject(request);            outputStream.flush();            inputStream = new ObjectInputStream(socket.getInputStream());            result = inputStream.readObject();        } catch (IOException | ClassNotFoundException e) {            e.printStackTrace();        } finally {            try {                if (socket != null) {                    socket.close();                }            } catch (IOException e) {                e.printStackTrace();            }            try {                if (outputStream != null) {                    outputStream.close();                }            } catch (IOException e) {                e.printStackTrace();            }            try {                if (inputStream != null) {                    inputStream.close();                }            } catch (IOException e) {                e.printStackTrace();            }        }        return result;    }}