package com.xgdfin.rpc.channel;import java.lang.reflect.Proxy;/** Copyright (C) 2017  ShenZhen xinLianZhengXin Co.,Ltd All Rights Reserved.* 未经本公司正式书面同意，其他任何个人、团体不得使用、复制、修改或发布本软件.* 版权所有深圳市信联征信有限公司 http://www.credlink.com/*/public class RpcChannelClientProxy {    /** 客户端服务代理*/    public <T> T clientProxy(Class<?> clazz, String host, int port) {        Class<?>[] interfaces = clazz.isInterface()                ? new Class<?>[]{clazz} : clazz.getInterfaces();        return (T) Proxy.newProxyInstance(clazz.getClassLoader(), interfaces, new RpcChannelClientInvocationHandler(clazz, host, port));    }}